var nameImage,font,flowers=[],images=[],flowerCount=400,sketchNode=document.getElementById("splash-sketch-container");function mouseMoved(){let e=createVector(mouseX,mouseY);for(let s of flowers){let t=s.pos.dist(e);if(t<200){let o=p5.Vector.sub(s.pos,e);o.normalize().mult(4*(1-t/200)),o.z=0,s.pos.add(o)}}}function preload(){let e=loadImage("assets/img/flower.png");images.push(e),e=loadImage("assets/img/zinnia.png"),images.push(e),nameImage=loadImage("assets/img/name.png")}function setup(){createCanvas(window.innerWidth,window.innerHeight).parent(sketchNode),fill(255),stroke("#0e0e0e"),strokeWeight(3),frameRate(60),imageMode(CENTER),textAlign(CENTER,CENTER),nameImage.resize(.8*width,0);for(let e=0;e<flowerCount;e++)flowers.push(new Flower)}function draw(){background("#0e0e0e");for(let e of flowers)e.step(),e.show();image(nameImage,width/2,height/2)}class Flower{constructor(){this.regen()}regen(){this.pos=createVector(random(width),random(-height),random(TWO_PI)),this.vel=createVector(0,random(.1,2),random(-.01,.01)),this.image=random(images)}step(){this.vel.x=noise(.003*this.pos.x,.003*this.pos.y,frameCount/100)-.5,this.pos.add(this.vel),this.pos.y>height+this.image.height&&this.regen()}show(){push(),translate(this.pos.x,this.pos.y),scale(.1+noise(.01*this.pos.x,.003*this.pos.y)/2),rotate(this.pos.z),image(this.image,0,0),pop()}}